mapper.insert:
INSERT INTO EVENT_TBL 
VALUES (SEQ_EVENT_NO.NEXTVAL, DEFAULT, #{eventTitle}, #{eventFileName}, #{eventFileRename}, #{eventFilePath}, #{eventFileLength}
		,#{eventStartDate}, #{eventEndDate}, #{eventPrecautions}, #{eventInquiries}, DEFAULT, DEFAULT, DEFAULT)

mapper.update:
UPDATE EVENT_TBL SET EVENT_TITLE = #{eventTitle }, EVENT_FILE_NAME = #{eventFileName }, EVENT_FILE_RENAME = #{eventFileRename }, EVENT_FILE_PATH = #{eventFilePath }
	, EVENT_FILE_LENGTH = #{eventFileLength }, EVENT_START_DATE = #{eventStartDate }, EVENT_END_DATE = #{eventEndDate }, EVENT_PRECAUTIONS = #{eventPrecautions}
	, EVENT_INQUIRIES = #{eventInquiries }, EVENT_UPDATE_DATE = DEFAULT WHERE EVENT_NO = #{eventNo }

UPDATE TRAVEL_TBL
SET EVENT_STATUS = 'N'
WHERE EVENT_NO = #{eventNo}
